<div id="view-item" class="modal fade">
	<div class="modal-dialog">
		<div class="modal-content">
			<div class="modal-header">
				<button type="button" class="close" data-dismiss="modal">&times;</button>
			</div>

			<div class="modal-body">

				<div class="row">
					<div class="col-md-4">
						<img class="img-responsive item-view" id="img-item">
					</div>
					<div class="col-md-8">
						<div class="form-group">
							<label class="col-lg-3">Item Code</label>
							<div class="col-lg-9">
								<input type="text" class="form-control" readonly id="code-item">
							</div>
						</div>
						<br>
						<div class="form-group">
						  <label class="col-lg-3">Item Name</label>
							<div class="col-lg-9">
								<input type="text" class="form-control" readonly id="name-item">
							</div>
						</div>
						<br>
						<div class="form-group">
							<label class="col-lg-3">Item Type</label>
							<div class="col-lg-9">
								<input type="text" class="form-control" readonly id="ctg-type">
							</div>
						</div>
						<br>
						<div class="form-group">
							<label class="col-lg-3">Item Color</label>
							<div class="col-lg-9">
								<input type="text" class="form-control" readonly id="ctg-color">
							</div>
						</div>
						<br>
						<div class="form-group">
							<label class="col-lg-3">Item Status</label>
							<div class="col-lg-9">
								<input type="text" class="form-control" readonly id="status-item">
							</div>
						</div>
						<br>
						<div class="form-group">
							<label class="col-lg-3">Item Price</label>
							<div class="col-lg-9">
								<input type="text" class="form-control" readonly id="price-item">
							</div>
						</div>
					</div>
				</div>

      </div>

			<div class="modal-footer">
				<button type="button" class="btn btn-link" data-dismiss="modal">Close</button>
			</div>
		</div>
	</div>
</div>
